import React, { useState } from 'react';
import Card from '../../../components/shared/Card/Card';
import Button from '../../../components/shared/Button/Button';
import {firebase} from '../../../base';
import styles from './StepName.module.css';
import { Link } from 'react-router-dom';
import { withRouter } from "react-router";


const StepName = ({ onNext }) => {
    const  name  = "";
    const [fullname, setFullname] = useState(name);
    const [Cpassword , setCpassword] = useState('');
    const [password , setpassword] = useState('');
    const [email , setemail] = useState('');

    function goNext () {
        if (!fullname) {
            return;
        }
        if(password === Cpassword){
            var number = window.phoneNumber;
            window.userName = fullname;
            window.phoneNumber = number;
            window.FinalEmail = email;
            window.password = password;
              
            var user = firebase.auth().currentUser;   
            if(user){
                user.updateProfile({
                    phoneNumber: number,
                })
            }
        } 
        
        else{
            alert("Confirm Password does not match!");
        }
    }
    return (
        <>
            <div className={styles.center}>
            <Card title="User Details" icon="goggle-emoji">
            <input className={styles.input}  type="text" placeholder="Your name" onChange={(e)=>{setFullname(e.target.value)}}/>  
                <br/>
            <input className={styles.input}  type="email" placeholder="Email" onChange={(e)=>{setemail(e.target.value)}}/>  
                <br/>
            <input className={styles.input}  type="password" placeholder="Password" onChange={(e)=>{setpassword(e.target.value)}}/>  
                <br/>
            <input className={styles.input}  type="password" placeholder="Confirm Password" onChange={(e)=>{setCpassword(e.target.value)}}/>
                <br />                
            <div>
                <Button onNext={goNext} text="Next" />
            </div>

            <p className={styles.paragraph}>
                By entering your email, youâ€™re agreeing to our
                Terms of Service and Privacy Policy. People use real names at codershouse :)
            </p>

            <div className={styles.signins}>
                <span className={styles.hasInvites}> Have an invite text?</span>
                <Link className={styles.signinLinks} to="./login">SignIn</Link>
            </div>

            </Card>
            </div>
        </>
    );
};

export default withRouter(StepName);
