import React, { useState, useContext } from "react";
import { withRouter, Redirect } from "react-router";
import {firebase,auth} from "../../../base";
import { AuthContext } from "../../../Auth.js";
import Card from '../../../components/shared/Card/Card';
import Button from '../../../components/shared/Button/Button';
import styles from '../SignupEmail.module.css';
import styless from './Email-textInput.module.css';
import { Link } from 'react-router-dom';

const EmailSignUp = ({ history }) => {
    const [email , setemail] = useState('');
    const [password , setpassword] = useState('');
    const [Cpassword , setCpassword] = useState('');

    
    async function SignUp(){
            if(password === Cpassword){
                try{
                    await firebase.auth().createUserWithEmailAndPassword(email.trim() , password)
                    .then(resp => { 
                        window.userId = resp.user.uid;
                        window.email = resp.user.email;                    
                        firebase.auth().onAuthStateChanged(function(user) {
                            if(user){
                                // user.updatePhoneNumber(resp);
                                user.updateProfile({
                                    displayName: window.userName,
                                    phoneNumber: window.phoneNumber,
                                    emailVerified: true,
                                    photoURL: "https://static3.depositphotos.com/1004996/226/i/950/depositphotos_2260019-stock-photo-glowing-font-shiny-letter-s.jpg"
                                })
                                .then(() => {
                                    console.log(window.email,window.userId,window.userName,window.phoneNumber);
                                    resp.user.sendEmailVerification();
                                    alert("Email Sent")
                                })
                                .catch((error) => {
                                    console.log(error);
                                }); 
                                
                            }
                                
                        })
                    });
                    history.push('/room')  
                    
                } 
                catch (error) {
                    alert(error);
                };                    
            }
            else{
                alert("Confirm Password does not match!");
            }
        };
        
    
    const { currentUser } = useContext(AuthContext);
    if (currentUser) {
        return <Redirect to="/room" />;
    }

    return (
        <Card title="Signup with Email" icon="email-emoji">

            <input className={styless.input}  type="email" placeholder="Email" onChange={(e)=>{setemail(e.target.value)}}/>  
                <br/>
            <input className={styless.input}  type="password" placeholder="Password" onChange={(e)=>{setpassword(e.target.value)}}/>  
                <br/>
            <input className={styless.input}  type="password" placeholder="Confirm Password" onChange={(e)=>{setCpassword(e.target.value)}}/>
                <br />
            <div className={styles.actionButton}>
                <Button text="SignUp" onNext={SignUp}></Button> 
            </div>
                
            <p className={styles.bottomParagraph}>
                By entering your email, you’re agreeing to our
                Terms of Service and Privacy Policy. Thanks! 
            </p>

            <div className={styless.signins}>
                    <span className={styless.hasInvites}> Have an invite text?</span>
                    <Link className={styless.signinLinks} to="./login">SignIn</Link>
            </div>
        </Card>
    )
   
}

export default withRouter(EmailSignUp);
