import React from 'react'
import { db } from '../../base';

class Read extends React.Component {

    state={
        users: null, 
    }
    componentDidMount(){
        console.log("Mounted");
        db.collection('users')
          .get('tSkn9zBsJkFeV2wH5Ye1')
          .then( snapshot =>{
              const users =[];
              snapshot.forEach(doc =>{
              const data = doc.data()
              users.push(data)
          })
            this.setState({users:users})
              console.log(snapshot)
          })
          .catch(error => console.log(error));
    }

    render() {

        return (
            <div>
                User Data
                {
                    this.state.users && 
                    this.state.users.map (user =>{
                        return(
                            <div>
                                <p>{user.name}</p>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}

export default Read
