import React, { useState } from 'react';
import Card from '../../../components/shared/Card/Card';
import Button from '../../../components/shared/Button/Button';
import styles from './StepAvatar.module.css';

const StepAvatar = ({ onNext }) => {
    const [image, setImage] = useState('/images/monkey-avatar.png');


    function captureImage(e) {
        const file = e.target.files[0];
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onloadend = function () {
            setImage(reader.result);
        };
    }

    var user = {
        name: window.userName,
        uid: window.userId,
        email: window.email
    }
        
    function writeUserData(user) {
        console.log(user.uid);
        firebase.database().ref('users/' + window.userId).set(user).catch(error => {
            console.log(error.message)
        });
    }
        // const avatarStgRef = firebase.storage().ref("Usuarios/" + window.userId + "/avatar.png");
        // avatarStgRef.put(file).then(function(snapshot){
        //     snapshot.ref.getDownloadURL().then(function(url){  // Now I can use url
        //         user.updateProfile({
        //             photoURL: image       // <- URL from uploaded photo.
        //         }).then(function(){
        //             firebase.database().ref("Usuarios/" + user.uid).set({
        //               "photoUri": url   // <- URL from uploaded photo.
        //             });
        //         });
        //     });
        // });


    async function submit() {
        try {
            writeUserData(user);
            console.log(`${window.userName}`);
            onNext();

        } catch (err) {
            console.log(err);
            // history.push('/signup');
        }
    }

    







    return (
        <>
            <Card title={`Okay, ${window.userName}`} icon="monkey-emoji">
                <p className={styles.subHeading}>Howâ€™s this photo?</p>
                <div className={styles.avatarWrapper}>
                    <img
                        className={styles.avatarImage}
                        src={image}
                        alt="avatar"
                    />
                </div>
                <div>
                    <input
                        onChange={captureImage}
                        id="avatarInput"
                        type="file"
                        className={styles.avatarInput}
                    />
                    <label className={styles.avatarLabel} htmlFor="avatarInput">
                        Choose a different photo
                    </label>
                </div>
                <div>
                    <Button onNext={submit} text="Next" />
                </div>
            </Card>
        </>
    );
};

export default StepAvatar;
