import React, {useState} from 'react';
import Phone from './Phone/Phone';
import EmailSignup from './Email/EmailSignUp';
import styles from  './StepPhoneEmail.module.css';
const phoneemailMap = {
    phone: Phone,
    email: EmailSignup,
}


const SignupStepPhoneEmail = ({onNext}) => {

    const [type, setType] = useState('email');
    const Type = phoneemailMap[type];

    return (
        <>
            <div className={styles.cardwrapper}>
                <div>
                    <div className={styles.buttonwrap}>

                        {/* <button  className={`${styles.tabButton} ${type === 'phone' ? styles.active : ''}`} onClick={() => setType('phone')}>
                             <img src="/images/phone-white.png" alt="Phone" />
                        </button> */}
                        <button className={`${styles.tabButton} ${type === 'email' ? styles.active : ''}`} onClick={() => setType('email')}>
                            <img src="/images/mail-white.png" alt="Email" />
                        </button>
                        
                    </div>
                    <Type onNext={onNext} />
                </div>
            </div>
        </>

    )
}

export default SignupStepPhoneEmail
