import React, { useState, useContext } from "react";
import { withRouter, Redirect } from "react-router";
import {firebase} from "../../../../base";
import { AuthContext } from "../../../../Auth.js";
import Card from '../../../../components/shared/Card/Card';
import Button from '../../../../components/shared/Button/Button';
import styles from '../StepPhoneEmail.module.css';
import styless from './Email-textInput.module.css';
import { Link } from 'react-router-dom';


const EmailLogin = ({ history }) => {
    const [email , setemail] = useState('');
    const [password , setpassword] = useState('');

        const Login = () =>{
        try {
            firebase.auth().signInWithEmailAndPassword(email.trim() , password);
            history.push("/");
        } 
        catch (error) {
        alert(error);
       };
    };
    
    const { currentUser } = useContext(AuthContext);
    if (currentUser) {
        return <Redirect to="/" />;
    }

    return (
        <Card title="Enter Your Email Id" icon="email-emoji">

            <input className={styless.input}  type="email" onChange={(e)=>{setemail(e.target.value)}}/>  
                <br/>
                <br/>
            <input className={styless.input}  type="password" onChange={(e)=>{setpassword(e.target.value)}}/>  
                <br/>
                <br/>
            <div className={styles.actionButton}>
                <Button text="Login" onNext={Login}></Button> 
            </div>
                
            <p className={styles.bottomParagraph}>
                By entering your email, youâ€™re agreeing to our
                Terms of Service and Privacy Policy. Thanks! 
            </p>

            <div className={styless.signins}>
                    <span className={styless.hasInvites}> New user?</span>
                    <Link className={styless.signinLinks} to="./signup">Register Here</Link>
            </div>
        </Card>
    )
   
}

export default withRouter(EmailLogin);
