import React, { useState,useContext } from "react";
import { withRouter, Redirect } from "react-router";
import {firebase,auth} from "../../../base";
import { AuthContext } from "../../../Auth.js";
import Card from '../../../components/shared/Card/Card'
import Button from '../../../components/shared/Button/Button';
import TextInput from '../../../components/shared/textInput/TextInput';
import styles from '../LoginPhoneEmail.module.css';
import styless from '../Email/Email.module.css';
import { Link } from 'react-router-dom';

const Phone =  ({ history }) => {
    // Inputs
    const [phonenumber,setphonenumber] = useState('');
    const [otp,setotp] = useState('');
    const [show, setshow] = useState(false);
    const [final, setfinal] = useState('');

    // Signin
    const signinPhone = () => { 
        if (phonenumber === "" || (phonenumber.length < 10 && phonenumber.length > 10)){
            alert("Enter valid Phonenumber!");
            return;
        };
        let verify = new firebase.auth.RecaptchaVerifier('recaptcha-container');
        const Finalphonenumber = "+91"+phonenumber;
        auth.signInWithPhoneNumber(Finalphonenumber, verify).then((result) => {
            setfinal(result);
            setshow(true);
        }),
        firebase.auth()
        .getUserByPhoneNumber(phoneNumber)
        .then((userRecord) => {
          // See the UserRecord reference doc for the contents of userRecord.
          console.log(`Successfully fetched user data:  ${userRecord.toJSON()}`);
        })
        .catch((error) => {
          console.log('Error fetching user data:', error);
        }),
        (resp => { 
            window.userId = resp.user.uid;
            window.email = resp.user.email;                    
            firebase.auth().onAuthStateChanged(function(user) {
                if(user){
                    user.updateProfile({
                        displayName: window.userName,
                        phoneNumber: window.window.PhoneNumber,
                        photoURL: "https://example.com/jane-q-user/profile.jpg"
                    }).then(() => {
                        alert("Done")
                      }).catch((error) => {
                       console.log(error);
                      }); 
                }      
            })
        })
        .catch((err) => {
            alert(err);
            window.location.reload()
        });
    }

    // Validate OTP
    const ValidateOtp = () => {
        if (otp === null || final === null)
            return;
        final.confirm(otp).then((result) => {
            history.push('/room');
        }).catch((error) => {
            alert("Enter Valid Code!");
        })
    }

    console.log(otp);

    const { currentUser } = useContext(AuthContext);
    if (currentUser) {
        return <Redirect to="/" />;
    }

    return (
        <>
            <div style={{ display: !show ? "block" : "none" }}>
                <Card title="Enter Your Phone Number" icon="phone">      
                <TextInput value={phonenumber} onChange={(e) => setphonenumber(e.target.value)} Placeholder="9478234565"/>  
                        <br/>
                        <div id="recaptcha-container"></div>
                        <div className={styles.actionButton}> 
                            <div id="recaptcha"></div> 
                            <Button required="True"onNext={signinPhone} text="Send OTP" ></Button> 
                        </div>
                        <p className={styles.bottomParagraph}>
                            By entering your number, you’re agreeing to our <span className={styles.centerText}> Terms of
                            Service and Privacy Policy. Thanks! </span>
                        </p>
                        <div className={styless.signins}>
                            <span className={styless.hasInvites}> New user?</span>
                            <Link className={styless.signinLinks} to="./activate">Register Here</Link>
                        </div>
                </Card>
            </div>

            <div style={{ display: show ? "block" : "none" }}>
                <Card title="Enter the code we just texted you" icon="lock-emoji">

                        <TextInput  value={otp} onChange={(e)=> setotp(e.target.value)}/>
                        <br /> 
                        <div className={styles.actionButton}>
                            <Button onNext={ValidateOtp} text="Verify"></Button> 
                        </div>
                    
                        <p className={styles.bottomParagraph}>
                            By entering your number, you’re agreeing to our <span className={styles.centerText}> Terms of
                            Service and Privacy Policy. Thanks! </span>
                        </p>
                </Card>
            </div>
        </>
    )
}

export default withRouter(Phone);
