import React, { useCallback, useContext } from "react";
import { withRouter, Redirect } from "react-router";
import app from "../../../../base";
import { AuthContext } from "../../../../Auth.js";
import Card from '../../../../components/shared/Card/Card';
import Button from '../../../../components/shared/Button/Button';
import styles from '../StepPhoneEmail.module.css';
import styless from './Email-textInput.module.css';


const [email , setemail] = useState('');
const [password , setpassword] = useState('');
const Login = ({ history }) => {
  const handleLogin = useCallback(
    async event => {
      event.preventDefault();
      try {
        await app
          .auth()
          .signInWithEmailAndPassword(email.value, password.value);
        history.push("/");
      } catch (error) {
        alert(error);
      }
    },
    [history]
  );
    
    const { currentUser } = useContext(AuthContext);
    if (currentUser) {
        return <Redirect to="/" />;
      }

    return (
        <Card title="Enter Your Email Id" icon="email-emoji">

            <input className={styless.input} Placeholder="Email" type="email" onChange={(e)=>{setemail(e.target.value)}}/>  
                <br/>
                <br/>
            <input className={styless.input} Placeholder="Password" type="password" onChange={(e)=>{setpassword(e.target.value)}}/>  
                <br/>
                <br/>
            <div className={styles.actionButton}>
                <Button text="Log in" onNext={handleLogin}></Button> 
            </div>
                
            <p className={styles.bottomParagraph}>
                By entering your email, youâ€™re agreeing to our
                Terms of Service and Privacy Policy. Thanks! 
            </p>
        </Card>
    )
   
}

export default withRouter(Login);
