import React, { useState } from 'react';
import {useHistory } from 'react-router-dom';
import Card from '../../../components/shared/Card/Card';
import Button from '../../../components/shared/Button/Button';
import styles from './StepAvatar.module.css';
import {firebase, admin} from '../../../base';

const StepAvatar = () => {
    
    const [image, setImage] = useState('/images/monkey-avatar.png');
    const history = useHistory();

    function captureImage(e) {
        const file = e.target.files[0];
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onloadend = function () {
            setImage(reader.result);
        };
    }

    var number = window.phoneNumber;
    window.phoneNumber = number;
    firebase.auth().createUserWithEmailAndPassword(window.FinalEmail.trim() , window.password)
    .then(resp => { 
        resp.user.sendEmailVerification();
        console.log(window.phoneNumber);
        var user = firebase.auth().currentUser;
        if (user) { 

            admin.auth().updateUser(uid, {
                phoneNumber: "+11234567890",
              }).then(function(userRecord) {
                // User updated. You can now get this from client:
                // FirebaseAuth.getCurrentUser().getPhoneNumber();
              }).catch(function(error) {
                // Error occurred.
              });

            user.updateProfile({
                displayName: window.userName,
                phoneNumber: window.phoneNumber,
                email: window.FinalEmail,
                photoURL: "https://static3.depositphotos.com/1004996/226/i/950/depositphotos_2260019-stock-photo-glowing-font-shiny-letter-s.jpg",
            })
            .then(() => {
                console.log("Done");
            })
            .catch((error) => {
                console.log(error);
            });
        }
    });
    async function submit() {
        try {
            // var user = firebase.auth().currentUser;
            // if (user) { 
            //     user.updateProfile({
            //         displayName: window.userName,
            //         phoneNumber: window.phoneNumber,
            //         email: window.FinalEmail,
            //         photoURL: "https://static3.depositphotos.com/1004996/226/i/950/depositphotos_2260019-stock-photo-glowing-font-shiny-letter-s.jpg",
            //     })
            //     .then(() => {
            //         console.log("Done");
            //     })
            //     .catch((error) => {
            //         console.log(error);
            //     });
            // }
                
            console.log(`Number: ${window.phoneNumber}`);
            console.log(`Name: ${window.userName}`);
            history.push('/room');
        } catch (err) {
            console.log(err);
            history.push('/activate');
        }
    }

    return (
        <>  
            <div className={styles.center}>
            <Card title={`Okay, ${window.userName}`} icon="monkey-emoji">
                <p className={styles.subHeading}>How’s this photo?</p>
                <div className={styles.avatarWrapper}>
                    <img
                        className={styles.avatarImage}
                        src={image}
                        alt="avatar"
                    />
                </div>
                <div>
                    <input
                        onChange={captureImage}
                        id="avatarInput"
                        type="file"
                        className={styles.avatarInput}
                    />
                    <label className={styles.avatarLabel} htmlFor="avatarInput">
                        Choose a different photo
                    </label>
                </div>
                <div>
                    <Button onNext={submit} text="Next" />
                </div>
            </Card>
            </div>
        </>
    );
};

export default StepAvatar;
