import React, { useState } from 'react';
import {useHistory } from 'react-router-dom';
import Card from '../../../components/shared/Card/Card';
import Button from '../../../components/shared/Button/Button';
import styles from './StepAvatar.module.css';
import axios from "axios";
import {firebase} from '../../../base';



const StepAvatar = () => {
    
    const [image, setImage] = useState('/images/monkey-avatar.png');
    const history = useHistory();
    var number = window.phoneNumber;
    window.phoneNumber = number;

    function captureImage(e) {
        const file = e.target.files[0];
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onloadend = function () {
            setImage(reader.result);
        };
    }

    async function createUser(phoneNumber, userName, email, password, photoURL){
        axios.post(`http://localhost:4000/activate`, { phoneNumber:phoneNumber, userName:userName, email:email, password:password, photoURL:photoURL })
        .then(res => {
          console.log(res);
          console.log(res.data);
          console.log(res.status);
        })
    }

    function varification(){
        firebase.auth().currentUser.sendEmailVerification()
        .then(() => {
            console.log("Varification mail Sent.");
        });
    }
    

    async function submit() {
        try {
            
            var phoneNumber = window.phoneNumber;
            var userName = window.userName;
            var email = window.FinalEmail;
            var password = window.password;
            var photoURL = "https://static3.depositphotos.com/1004996/226/i/950/depositphotos_2260019-stock-photo-glowing-font-shiny-letter-s.jpg";
            createUser(phoneNumber, userName, email, password, photoURL);
            varification();
            history.push('/room');
            
        } catch (err) {
            console.log(err);
            history.push('/activate');
        }
    }

    return (
        <>  
            <div className={styles.center}>
            <Card title={`Okay, ${window.userName}`} icon="monkey-emoji">
                <p className={styles.subHeading}>Howâ€™s this photo?</p>
                <div className={styles.avatarWrapper}>
                    <img
                        className={styles.avatarImage}
                        src={image}
                        alt="avatar"
                    />
                </div>
                <div>
                    <input
                        onChange={captureImage}
                        id="avatarInput"
                        type="file"
                        className={styles.avatarInput}
                    />
                    <label className={styles.avatarLabel} htmlFor="avatarInput">
                        Choose a different photo
                    </label>
                </div>
                <div>
                    <Button onNext={submit} text="Next" />
                </div>
            </Card>
            </div>
        </>
    );
};

export default StepAvatar;
