import React , {useState} from 'react';
import {useHistory } from 'react-router-dom';
import Card from '../../../components/shared/Card/Card';
import Button from '../../../components/shared/Button/Button';
import styles from './StepAvatar.module.css';
import axios from "axios";
import {storage} from "../../../base";
import {v4 as uuid} from "uuid";

const StepAvatar = () => {
    
    const [image, setImage] = useState('/images/monkey-avatar.png'); 
    const [file, setFile] = useState('/images/monkey-avatar.png');
    const history = useHistory();
    
    var phoneNumber = window.phoneNumber;
    var userName = window.userName;
    var email = window.FinalEmail;
    var password = window.password;


    const captureImage = e => {
        setFile(e.target.files[0]);
        const reader = new FileReader();
        reader.readAsDataURL(e.target.files[0]);
        reader.onloadend = function () {
            setImage(reader.result);
        };
    };
    
    const handleUpload = async ()=>{
        const id = uuid();
        const imagesRef = await storage.ref('images').child(id);
        await imagesRef.put(file); 
        await imagesRef.getDownloadURL().then((url)=>{
            createUser(phoneNumber, userName, email, password, url);
        })   
      };
    
    async function createUser(phoneNumber, userName, email, password, url ){
        axios.post(`http://localhost:4000/activate`, { phoneNumber:phoneNumber, userName:userName, email:email, password:password, photoURL: url })
        .then(res => {
          console.log(res);
          console.log(res.data);
          console.log(res.status);
        })
    }

    async function submit() {
        try {
            handleUpload();
            alert("verification link sent to your registered mail id.");
            history.push('/room');
            
        } catch (err) {
            console.log(err);
            history.push('/activate');
        }
    }

    return (
        <>  
            <div className={styles.center}>
            <Card title={`Okay, ${window.userName}`} icon="monkey-emoji">
                <p className={styles.subHeading}>How’s this photo?</p>
                <div className={styles.avatarWrapper}>
                    <img
                        className={styles.avatarImage}
                        src={image}
                        alt="avatar"
                    />
                </div>
                <div>
                    <input
                        accept="image/*"
                        onChange={captureImage}
                        id="avatarInput"
                        type="file"
                        className={styles.avatarInput}
                    />
                    <label className={styles.avatarLabel} htmlFor="avatarInput">
                        Choose a different photo
                    </label>
                </div>
                <div>
                    <Button onNext={submit} text="Next" />
                </div>
            </Card>
            </div>
        </>
    );
};

export default StepAvatar;
